import React from 'react';
import Navbars from '../NavBars';
import Carousel from '../Carousel';
import Footer from '../Footer';
import store from '../../redux/store';

class Carts extends React.Component {
  constructor() {
    super();
    this.state = store.getState();
    console.log(this.state.orderId, 'orderId');
  }
  render() {
    const orderId = this.state.orderId;
    return (
      <div>
        <Navbars />
        <Carousel />
        <div className="mainContentbg" style={{padding: '100px'}}>
          <div className="container warrantlyBorder text-white text-center">
            <h1 className="p-3">購物車</h1>
            <div className="row text-white text-center">
              <div className="col-sm-12">
                <table class="table text-white">
                  <thead>
                    <tr>
                      <th scope="col">#</th>
                      <th scope="col">商品</th>
                      <th scope="col">價格</th>
                      <th scope="col">取消</th>
                    </tr>
                  </thead>
                  {orderId.map((item, key) => (
                    <tbody>
                      <tr>
                        <th scope="row">{key + 1}</th>
                        <td>{item.name}</td>
                        <td>{item.price}</td>
                        <td>
                          <button
                            type="button"
                            class="btn btn-outline-danger btn-sm"
                          >
                            <i class="far fa-trash-alt"></i>
                          </button>
                        </td>
                      </tr>
                    </tbody>
                  ))}
                </table>
              </div>
            </div>
            <h5 class="text-right pr-5">總計:1000元</h5>
          </div>
        </div>
        <Footer />
      </div>
    );
  }
}

export default Carts;
