import React from 'react';
import Navbars from '../NavBars';
import Carousel from '../Carousel';
import Footer from '../Footer';
import { keyboard } from '../../Config';
import store from '../../redux/store';
import { Link } from 'react-router-dom';
import { Card } from 'react-bootstrap';

class KeyboardModal extends React.Component {
  constructor() {
    super();
    this.state = store.getState();
    console.log(this.state.itemId);
  }
  render() {
    return (
      <div>
        <Navbars />
        <Carousel />
        <h1>{keyboard[this.state.itemId - 1].id}</h1>
        <div className="mainContentbg">
          <div className="container">
            <div className="row">
              <div className="col-sm-6">
                <Card className="bg-dark text-white imgBox">
                  <Card.Img
                    src="https://images.unsplash.com/photo-1563191911-e65f8655ebf9?ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&ixlib=rb-1.2.1&auto=format&fit=crop&w=1050&q=80"
                    alt="Card image"
                    className="img"
                  />
                  <Card.ImgOverlay>
                    <Card.Title>鍵盤</Card.Title>
                    <Card.Text>
                      This is a wider card with supporting text below as a
                      natural lead-in to additional content. This content is a
                      little bit longer.
                    </Card.Text>
                    <Card.Text>Last updated 3 mins ago</Card.Text>
                  </Card.ImgOverlay>
                </Card>
              </div>
              <div className="col-sm-6 text-white">
                <Link className="link">首頁</Link>
                >>
                <Link className="link">鍵盤</Link>
                >>
                <Link className="link">{keyboard[this.state.itemId].name}</Link>
              </div>
            </div>
          </div>
        </div>
        <Footer />
      </div>
    );
  }
}

export default KeyboardModal;
