import React from 'react';
import { Card, Button } from 'react-bootstrap';

import Navbars from '../NavBars';
import Carousel from '../Carousel';
import Footer from '../Footer';
import { keyboard } from '../../Config/';

class Keyboard extends React.Component {
  constructor() {
    super();
    const abc = keyboard.map((item, value)=>{
      console.log(item.name, 'item');
      console.log(value, 'value');
    })
  }
  productInfoBtn(value, items) {
      console.log(value,items, 'clikc拉')
  }
  render() {
    return (
      <div>
        <Navbars />
        <Carousel />
        <div className="mainContentbg pt-5">
          <div className="container">
            <div className="text-center text-white pb-5">
              <h1>鍵盤 - KeyBoards</h1>
            </div>
            <div className="row">
<div>
{keyboard.map((items, value)=>{
                <div className="col-sm-4 text-center">
                <Card style={{ border: 'none' }} className="cardItem text-white" bg='dark'>
                  <Card.Img
                    variant="top"
                    src="https://images.unsplash.com/photo-1616837662891-c62d5fe6418e?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1050&q=80"
                  />
                  <Card.Body>
                    <Card.Title>{items.name}</Card.Title>
                    <Card.Text>
                      基礎介紹
                    </Card.Text>
                    <div className="shopBtn">
                    <Button variant="primary" className="itemBtn btn-light" onClick={this.productInfoBtn(value, items)}>查看詳情</Button>
                    </div>
                  </Card.Body>
                </Card>
              </div>
              {console.log(items.name, 'iyems')}
              {console.log(value, 'value')}
              })}
</div>

            </div>
          </div>
        </div>

        <Footer />
      </div>
    );
  }
}

export default Keyboard;
